contracts/interfaces/IMiniMetoken.sol.content:
// https://github.com/Giveth/minime/blob/master/contracts/MiniMeToken.sol
interface IMiniMetoken {
  function balanceOfAt(address account, uint256 blockNumber)
    external
    view
    returns (uint96);

  function totalSupplyAt(uint256 blockNumber) external view returns (uint256);
}

contracts/interfaces/IGnosisSafe.sol.content:
interface IGnosisSafe {
  enum Operation {Call, DelegateCall}

  function approveHash(bytes32 hashToApprove) external;

  /**
   */
  function execTransaction(
    address to,
    uint256 value,
    bytes calldata data,
    Operation operation,
    uint256 safeTxGas,
    uint256 baseGas,
    uint256 gasPrice,
    address gasToken,
    address payable refundReceiver,
    bytes calldata signatures
  ) external payable returns (bool success);
}

contracts/interfaces/IERC20Snapshot.sol.content:
interface IERC20Snapshot {
  function balanceOfAt(address account, uint256 blockNumber)
    external
    view
    returns (uint96);

  function totalSupplyAt(uint256 blockNumber) external view returns (uint256);

  function snapshot() external returns (uint256);
}

